    return document;
}

function calc_baseline_emissions(document) {
    switch (document.H35) {
        case 'Greenfield power plants.':
            document.H36 = document.H40;
            break;
        case 'Capacity addition in wind, solar, wave or tidal power plants.':
            document.H36 = document.H43;
            break;
        case 'Capacity addition in hydro or geothermal power plants.': {
            if (document.H44.H51 === 'Before') {
                document.H36 = Math.max(
                    document.H44.H46 - (document.H44.H47 + document.H44.H48),
                    0
                );
            } else if (document.H44.H51 === 'On/After') {
                document.H36 = 0;
