                document.H36 = 0;
            } else {
                throw new Error(`Unknown option: ${document.H52.H60}`);
            }
            break;
        }
        case 'Retrofit, rehabilitation or replacement in hydro, solar, wind, geothermal, wave and tidal plants.': {
            if (document.H61.H63 === 'Yes' || document.H61.H69 === 'Before') {
                document.H36 = Math.max(
                    document.H61.H64 - (document.H61.H65 + document.H61.H66),
                    0
                );
            } else if (document.H61.H69 === 'On/After') {
                document.H36 = 0;
            } else {
                throw new Error(
                    `Unknown options: ${document.H61.H64}, ${document.H61.H69}`
                );
            }
            break;
        }
        case 'Retrofit, rehabilitation or replacement in biomass plants.': {
            document.H70.H74 = Math.max(document.H70.H76, document.H70.H77);
            if (document.H70.H72 === 'Yes' || document.H70.H80 === 'Before') {
                document.H36 = document.H70.H73 - document.H70.H74;
            } else if (document.H70.H80 === 'On/After') {
                document.H36 = 0;
