                throw new Error(
                    `Unknown options: ${document.H70.H72}, ${document.H70.H80}`
                );
            }
            break;
        }
        default:
            throw new Error(`Unknown option: ${document.H35}`);
    }
    document.H37 = document.tool_07.field5.field3; // Tool 07 Combined Margin G8
    document.H34 = document.H36 * document.H37;
    return document;
}

