function adjustValues(document) {
    const keys = Object.keys(document);
    for (const key of keys) {
        if (document[key] === undefined) {
            delete document[key];
        }
        if (typeof document[key] === 'number') {
            document[key] = isFinite(document[key]) ? document[key] : 0;
        }
        if (Array.isArray(document[key])) {
            for (const i in document[key]) {
                if (typeof document[key][i] === 'number') {
                    document[key][i] = isFinite(document[key][i])
                        ? document[key][i]
                        : 0;
                }
                if (
                    Object.prototype.toString.call(document[key][i]) ===
                    '[object Object]'
                ) {
                    document[key][i] = adjustValues(document[key][i]);
                }
            }
        }
        if (
            Object.prototype.toString.call(document[key]) === '[object Object]'
        ) {
            document[key] = adjustValues(document[key]);
        }
    }

    return document;
}

